document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll(".aash-btn");for(var t=0;t<e.length;t++)e[t].addEventListener("click",function(e){var t=document.createElement("span");t.classList.add("aash-ripple");var n=Math.max(this.offsetWidth,this.offsetHeight);t.style.width=t.style.height=n+"px";var i=this.getBoundingClientRect();t.style.left=e.clientX-i.left-n/2+"px";t.style.top=e.clientY-i.top-n/2+"px";this.appendChild(t);setTimeout(function(){t.remove()},600)})});