document.addEventListener("DOMContentLoaded", function () {
  var buttons = document.querySelectorAll(".aash-btn");
  buttons.forEach(btn => {
    btn.addEventListener("click", function (e) {
      var ripple = document.createElement("span");
      ripple.classList.add("aash-ripple");
      var size = Math.max(this.offsetWidth, this.offsetHeight);
      ripple.style.width = ripple.style.height = size + "px";
      var rect = this.getBoundingClientRect();
      ripple.style.left = e.clientX - rect.left - size/2 + "px";
      ripple.style.top = e.clientY - rect.top - size/2 + "px";
      this.appendChild(ripple);
      setTimeout(() => ripple.remove(), 600);
    });
  });

  // Modal open (attribute-driven)
  document.querySelectorAll('[data-toggle="aash-modal"]').forEach(btn => {
    btn.addEventListener('click', () => {
      const target = btn.getAttribute('data-target');
      const modal = document.querySelector(target);
      if (modal) modal.style.display = "flex";
    });
  });

  // Modal close (buttons with data-close)
  document.querySelectorAll('[data-close="aash-modal"]').forEach(btn => {
    btn.addEventListener('click', () => {
      const modal = btn.closest('.aash-modal');
      if (modal) modal.style.display = "none";
    });
  });

  // Optional: close modal on overlay click
  document.querySelectorAll('.aash-modal').forEach(modal => {
    modal.addEventListener('click', e => {
      if (e.target === modal) modal.style.display = "none";
    });
  });





  
});